SELECT  u.id_usuario, CONCAT(u.usu_nombre, '', u.usu_apellido) AS asesor, DATE_FORMAT(u.usu_fch_creacion, '%d/%m/%Y') AS fecha_ingreso, CASE WHEN u.usu_estado = 1 THEN 'Activo' ELSE 'Inactivo' END AS estado_usuario, af.nombre_analista AS analista FROM usuarios u LEFT JOIN analistas_freelances af ON af.id_usuario = u.usu_documento WHERE u.id_rol IN (19, 12, 11, 10, 1) AND af.id_analista = 1127609223